mapper.select:
select t.role_name from myblog.tb_role t where t.role_id in (
    select r.role_id
    from myblog.tb_role_user r
    where r.user_id = #{id}
)

select u.user_id, u.nickname, u.avatar, u.create_time, u.update_time, u.is_delete, u.is_silence, u.web_site, r.role_name from myblog.tb_user u,myblog.tb_role r,myblog.tb_role_user ru where u.user_id = ru.user_id and r.role_id = ru.role_id and u.is_delete = 0
and r.role_name = #{userQueryVO.roleName}
and u.nickname like concat('%',#{userQueryVO.nickname},'%')
limit #{userQueryVO.startIndex},#{userQueryVO.pageSize}

select u.user_id, u.nickname, u.avatar, u.username, u.intro, r.role_name from myblog.tb_user u, myblog.tb_role r, myblog.tb_role_user ru where u.user_id = ru.user_id and r.role_id = ru.role_id
and u.nickname like concat('%',#{nickname},'%')
limit #{current},#{size}

mapper.update:
update myblog.tb_user u set u.avatar = #{userDTO.avatar},
                            u.nickname = #{userDTO.nickname},
                            u.update_time = #{userDTO.updateTime}
where u.user_id = #{userDTO.userId}
